import React from 'react';
import { useForm, useToggle, upperFirst } from '@mantine/hooks';
import {
  TextInput,
  PasswordInput,
  Text,
  Paper,
  Group,
  PaperProps,
  Button,
  Divider,
  Checkbox,
  useMantineTheme,
} from '@mantine/core';
import { GoogleButton, TwitterButton } from '../SocialButtons/SocialButtons';

export function AuthenticationForm(props: PaperProps<'div'>) {
  const theme = useMantineTheme();
  const [type, toggle] = useToggle('login', ['login', 'register']);
  const form = useForm({
    initialValues: {
      email: '',
      name: '',
      password: '',
      terms: true,
    },

    validationRules: {
      email: (val) => /^\S+@\S+$/.test(val),
      password: (val) => val.length >= 6,
    },
  });

  return (
    <Paper
      shadow="sm"
      radius="md"
      padding="xl"
      {...props}
      style={{
        backgroundColor: theme.colorScheme === 'dark' ? theme.colors.dark[8] : theme.white,
        ...props.style,
      }}
    >
      <Text size="lg" weight={500}>
        Welcome to Mantine, {type} with
      </Text>

      <Group grow style={{ marginTop: 5, marginBottom: 15 }}>
        <GoogleButton radius="xl">Google</GoogleButton>
        <TwitterButton radius="xl">Twitter</TwitterButton>
      </Group>

      <Divider label="Or continue with email" labelPosition="center" />

      <form onSubmit={form.onSubmit(() => {})}>
        <Group direction="column" grow style={{ marginTop: 5 }}>
          {type === 'register' && (
            <TextInput
              label="Name"
              placeholder="Your name"
              value={form.values.name}
              onChange={(event) => form.setFieldValue('name', event.currentTarget.value)}
            />
          )}

          <TextInput
            required
            label="Email"
            placeholder="hello@mantine.dev"
            value={form.values.email}
            onChange={(event) => form.setFieldValue('email', event.currentTarget.value)}
            error={form.errors.email && 'Invalid email'}
          />

          <PasswordInput
            required
            label="Password"
            placeholder="Your password"
            value={form.values.password}
            onChange={(event) => form.setFieldValue('password', event.currentTarget.value)}
            error={form.errors.password && 'Password should include at least 6 characters'}
          />

          {type === 'register' && (
            <Checkbox
              label="I accept terms and conditions"
              checked={form.values.terms}
              onChange={(event) => form.setFieldValue('terms', event.currentTarget.checked)}
            />
          )}
        </Group>

        <Group position="apart" style={{ marginTop: 15 }}>
          <Button variant="link" color="gray" onClick={() => toggle()} size="xs">
            {type === 'register'
              ? 'Already have an account? Login'
              : "Don't have an account? Register"}
          </Button>
          <Button type="submit">{upperFirst(type)}</Button>
        </Group>
      </form>
    </Paper>
  );
}

import React from 'react';
import { GitHubLogoIcon } from '@modulz/radix-icons';
import { Button, ButtonProps, Group, useMantineTheme } from '@mantine/core';
import { GoogleIcon } from './GoogleIcon';
import { TwitterIcon } from './TwitterIcon';

export function GoogleButton({ style, ...props }: ButtonProps<'button'>) {
  const theme = useMantineTheme();
  return (
    <Button
      leftIcon={<GoogleIcon />}
      variant="outline"
      color="gray"
      style={{
        ...style,
        backgroundColor: theme.colorScheme === 'dark' ? theme.colors.dark[4] : theme.white,
        color: theme.colorScheme === 'dark' ? theme.colors.dark[0] : theme.black,
        borderColor: theme.colorScheme === 'dark' ? theme.colors.dark[4] : theme.colors.gray[3],
      }}
      {...props}
    />
  );
}

export function FacebookButton({ style, ...props }: ButtonProps<'button'>) {
  return <Button style={{ ...style, backgroundColor: '#4267B2', color: '#fff' }} {...props} />;
}

// Twitter button as anchor
export function TwitterButton({ style, component, ...others }: ButtonProps<'a'>) {
  const theme = useMantineTheme();
  return (
    <Button
      component="a"
      leftIcon={<TwitterIcon />}
      variant="outline"
      style={{
        ...style,
        backgroundColor: theme.colorScheme === 'dark' ? theme.colors.dark[4] : theme.white,
        borderColor: theme.colorScheme === 'dark' ? theme.colors.dark[4] : theme.colors.gray[3],
        color: theme.colorScheme === 'dark' ? theme.colors.dark[0] : theme.black,
      }}
      {...others}
    />
  );
}

export function GithubButton({ style, ...props }: ButtonProps<'button'>) {
  return (
    <Button
      {...props}
      leftIcon={<GitHubLogoIcon />}
      style={{
        ...style,
        backgroundColor: '#000',
        color: '#fff',
      }}
    />
  );
}

export function SocialButtons() {
  return (
    <Group position="center" withGutter>
      <GoogleButton>Continue with Google</GoogleButton>
      <TwitterButton href="https://twitter.com/mantinedev" target="_blank">
        Follow on Twitter
      </TwitterButton>
      <FacebookButton>Sign in with Facebook</FacebookButton>
      <GithubButton>View on GitHub</GithubButton>
    </Group>
  );
}
